package arrays;

public class VowelOrConsonant {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   char a='z';
   if(a=='a'||a=='i'||a=='e'||a=='o'||a=='u') {
	   System.out.println(a+"  is vowel");
   }
   else {
	   System.out.println(a+"is consonant");
   }
	}

}
